@namespace
class SpriteKind:
    Coin = SpriteKind.create()
    Spawn = SpriteKind.create()
    Meteor = SpriteKind.create()
    Indicator = SpriteKind.create()
    UFOBoss = SpriteKind.create()
    Cannon = SpriteKind.create()
    C1Switch = SpriteKind.create()
    C1Laser = SpriteKind.create()
    L4Boss = SpriteKind.create()

def on_overlap_tile(sprite, location):
    info.change_life_by(-1)
    for value in sprites.all_of_kind(SpriteKind.Meteor):
        value.destroy()
    startLevel()
scene.on_overlap_tile(SpriteKind.player, myTiles.tile2, on_overlap_tile)

def on_on_overlap(sprite, otherSprite):
    info.change_score_by(1)
    otherSprite.destroy()
    music.play_tone(659, music.beat(BeatFraction.EIGHTH))
    music.play_tone(988, music.beat(BeatFraction.EIGHTH))
sprites.on_overlap(SpriteKind.player, SpriteKind.Coin, on_on_overlap)

def on_on_overlap2(sprite, otherSprite):
    global UFOBossHealth
    otherSprite.destroy()
    if UFOBossHealth > 1:
        UFOBoss2.start_effect(effects.fire)
        UFOBossHealth += -1
        music.play_tone(147, music.beat(BeatFraction.SIXTEENTH))
        music.play_tone(139, music.beat(BeatFraction.SIXTEENTH))
        music.play_tone(131, music.beat(BeatFraction.SIXTEENTH))
    else:
        music.play_tone(196, music.beat(BeatFraction.SIXTEENTH))
        music.play_tone(175, music.beat(BeatFraction.SIXTEENTH))
        music.play_tone(156, music.beat(BeatFraction.SIXTEENTH))
        music.play_tone(131, music.beat(BeatFraction.QUARTER))
        UFOBoss2.start_effect(effects.disintegrate)
        sprite.destroy()
        info.change_score_by(5)
        UFOBossHealth = 0
sprites.on_overlap(SpriteKind.UFOBoss, SpriteKind.projectile, on_on_overlap2)

